load('problemFive.mat')load('wo.mat')weights_final = weights;spiking=0;inhibited=0;while((spiking==0)||(inhibited==0))    hold off        load('spikes2.mat');    gamma = 1;    Iapplied = temporalSummation(spikeTimes, Io, weights_final, taum, taus, dt);    Iapp=sum(Iapplied,1);    mP2 = AEF('RS', Iapp, dt);    plot(t,mP2,'red');    if(max(mP2)==0)        spiking=1;    else        [weights_final] = excitatoryResponseTraining(weights_final, taum, taus, gamma, Iapplied, mP2, dt, spikeTimes, Io);    end        load('spikes1.mat');    gamma = -1;    Iapplied = temporalSummation(spikeTimes, Io, weights_final, taum, taus, dt);    Iapp=sum(Iapplied,1);    mP1 = AEF('RS', Iapp, dt);    hold on;    plot(t,mP1,'blue');    if(max(mP1)~=0)        inhibited=1;    else        [weights_final] = inhibitoryResponseTraining(weights_final, taum, taus, gamma, Iapplied, mP1, dt, spikeTimes, Io);    endendsave wFinal_IE_Test.mat weights_finalclear;clc;